import pandas as pddef load_data(filepath):    return pd.read_csv(filepath)def clean_data(df):    df['Age'].fillna(df['Age'].median(), inplace=True)    for subject in ['Math', 'Science', 'English']:        df[subject].fillna(df[subject].mean(), inplace=True)    return dfdef summarize_data(df):    print("Average grades per subject:")    print(df[['Math', 'Science', 'English']].mean())    print("\nStudent count by class:")    print(df['Class'].value_counts())def main():    data_path = 'data/students.csv'    df = load_data(data_path)    df = clean_data(df)    summarize_data(df)if __name__ == "__main__":    main()