import pandas as pdimport osdef clean_data(input_path, output_path):    try:        df = pd.read_csv(input_path)        print(f"Original shape: {df.shape}")        df = df.drop_duplicates()        df = df.dropna()        df.columns = [col.strip().lower().replace(' ', '_') for col in df.columns]        df.to_csv(output_path, index=False)        print(f"Cleaned data saved to: {output_path}")        print(f"Final shape: {df.shape}")    except Exception as e:        print(f"Error: {e}")if __name__ == "__main__":    input_file = os.path.join("data", "school_data.csv")    output_file = os.path.join("data", "school_data_cleaned.csv")    clean_data(input_file, output_file)